package com.aipin.wx;import java.util.ArrayList;import java.util.List;import com.aipin.wx.autowx.WeixinRobot;import com.aipin.wx.autowx.utils.SleepUtils;public class TestThread extends Thread{	private List<WeixinRobot> robots=new ArrayList<WeixinRobot>();	@Override	public void run() {		while(true){			 for(WeixinRobot robot:robots){				 robot.receiveMsg();				 robot.handleMsg();			 }			SleepUtils.sleep(1000);		}	}	public void addRobot(WeixinRobot robot){		robots.add(robot);	}}